function human_civilian()
	local list

	local r=math.random()

	if (r<0.5) then
		list=human_generic
	else
		r=math.random()
		if (r<0.3) then
			list=authors
		elseif (r<0.6) then
			list=scientists
		else
			list=exporers
		end
	end

	local result=list[math.random(#list)]

	return result
end

