   local descriptor = descriptors[ rnd.rnd(1,#descriptors) ]
   local colour = colours[ rnd.rnd(1,#colours) ]
   local actor = actors[ rnd.rnd(1,#actors) ]
   local actorspecial = actorspecials[ rnd.rnd(1,#actorspecials) ]

   if rnd.rnd() < 0.25 then
      return actorspecial
   else
      local r = rnd.rnd()
      if r < 0.333 then
         return colour .. " " .. actor
      elseif r < 0.666 then
         return descriptor .. " " .. actor
      else
         return descriptor .. " " .. colour .. " " .. actor
      end
   end
end

function barbarians()
	local name=barbarians_ships[math.random(#barbarians_ships)]
  
  if name:find("#") then
    name=name:gsub("#",barbarians_random[math.random(#barbarians_random)])
  end
  
  return name
end

